import Layout from "../components/Layout";
import axios from "axios";

const SSRTest = (props) => {
  return (
    <Layout>
      {props.data.map(({ id, username }) => {
        return <li key={id}>{username}</li>;
      })}
      <h1>{props.flag}</h1>
    </Layout>
  );
};

SSRTest.getInitialProps = async function (props) {
  const response = await axios.get(
    "https://jsonplaceholder.typicode.com/users"
  );
  return {
    data: response.data,
    flag: props ? "server side rendering" : "client side rendering",
  };
};
export default SSRTest;
